#define BOOST_CONFIG_SUPPRESS_OUTDATED_MESSAGE
#pragma warning(disable:4244)
#pragma warning(disable:4503)
#pragma warning(disable:4996)
#include <iostream>
#include <string>
#include <vector>
#include <boost/algorithm/hex.hpp>
#include <boost/crc.hpp>

void check_image(std::string const& image_hex)
{
    std::vector<unsigned char> image(image_hex.size() / 2);
    boost::algorithm::unhex(image_hex, image.begin());

    boost::crc_32_type checksum_acumulator;
    checksum_acumulator.process_bytes(image.data(), image.size());

    std::cout << std::hex << checksum_acumulator.checksum() << std::endl;
}

int main(int argc, char* argv[])
{
    try
    {
        std::string red_fill = "";
        check_image(red_fill);

        std::string blue_line_from_dbg = "";
        check_image(blue_line_from_dbg);

        std::string blue_line_from_opt = "";
        check_image(blue_line_from_opt);
    }
    catch (std::runtime_error const& e)
    {
        std::cerr << e.what() << std::endl;
    }

    std::string out("type & ENTER ");
    std::cerr << out;
    std::cin >> out;
    return 0;
}
